sys_script_include.script:
var getEmailAddrv2 = Class.create();
getEmailAddrv2.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
  getEmail: function() {
	  gs.warn('user id: {0}', this.getParameter('sysparm_userID'));
    var userRecord = new GlideRecord("sys_user");
    userRecord.get(this.getParameter('sysparm_userID'));
	  gs.warn(userRecord.getRowCount());
    return userRecord.email + '';
  },

    type: 'getEmailAddrv2'
});
